import GraphComponent from './components/GraphComponentImport'
import ToggleButton from './components/ToggleButton'
import "aframe"
import RFGComponent from './components/RFGComponent'
import {ForceGraph3D} from 'react-force-graph'
import { useState } from 'react'
import { genRandomTree } from './random-data.js';
import './App.css'

function App() {
  const graphData=genRandomTree()
  console.log({graphData})
  const [showGraph, setShowGraph] = useState(true)
  return (
    <div>
      <ToggleButton setShowGraph={setShowGraph}></ToggleButton>
      <div className='graph-container'>
        <div className={`graph-overlay ${showGraph ? "visible" : "hidden"}`}id="sigma-container">
          <GraphComponent />
        </div>
        <div className={`graph-overlay ${showGraph ? "hidden" : "visible"}`}id="rfg-container">
          <ForceGraph3D graphData={graphData}/>
          <RFGComponent/>
        </div>
      </div>
    </div>
  )
}

export default App
